import React, { Component } from 'react';
import { BoxContainer, FormContainer, Input, SubmitButton, MutedLink, BoldLink } from "./common";
import { Marginer } from "../marginer";
import { AccountContext } from "./accountContent";
import { withFirebase } from '../Firebase/index';
import { compose } from 'recompose';
import { withRouter } from 'react-router-dom';




const INITIAL_STATE = {
    username: '',
    email: '',
    passwordOne: '',
    passwordTwo: '',
    error: null,
}

const SignUpPage = () => (
    <SignupForm />
)

class SignupFormBase extends Component {
    constructor(props) {
        super(props);

        this.state = { ...INITIAL_STATE };
    }

    onSubmit = event => {
        const { username, email, passwordOne } = this.state;

        this.props.Firebase
            .doCreateUser(email, passwordOne)
            .then(authUser => {
                this.setState({ ...INITIAL_STATE });
                this.props.history.push('/nutrition');
            })
            .catch(error => {
                this.setState({ error });
            });
        
        event.preventDefault();
    };

    onChange = event => {
        this.setState({ [event.target.name]: event.target.value })
    };
    
    render() {
        const {
            username,
            email,
            passwordOne,
            passwordTwo,
            error,
        } = this.state;

        const isInvalid =
            passwordOne !== passwordTwo ||
            passwordOne === '' ||
            email === '' ||
            username === '';

        return (
                <BoxContainer>
                    <FormContainer onSubmit={this.onSubmit}>
                        <Input name="username" type="text" value={username} placeholder="Username" onChange={this.onChange} />
                        <Input name="email" type="text" value={email} placeholder="Password" onChange={this.onChange} />
                        <Input name="passwordOne" type="password" value={passwordOne} placeholder="Password" onChange={this.onChange} />
                        <Input type="passwordTwo" type="password" value={passwordTwo} placeholder="Confirm Password" onChange={this.onChange} />
                    </FormContainer>
                    <Marginer direction="vertical" margin={0} />
                    <Marginer direction="vertical" margin="1.6em" />
                    <SubmitButton disabled={isInvalid} type="submit">Register</SubmitButton>
                    {error && <p>{error.message}</p>}
                    <Marginer direction="vertical" margin="0.2em" />
                    <MutedLink href="#">Already have an account? <BoldLink href="#" onClick={switchToSignin}>Signin</BoldLink></MutedLink>
                </BoxContainer>
        );
    }

}

const SignupForm = compose(withRouter,
    withFirebase,)(SignupFormBase);

export default SignUpPage;

export { SignupForm };
