import React, { useContext } from "react";
import { EasybaseProvider, useEasybase } from 'easybase-react';
import ebconfig from "../ebconfig.js"
import { BoxContainer, SubmitButton } from "./common";
import { Marginer } from "../marginer";
import { AccountContext } from "./accountContent";



export function SignedInForm(props) {
    const { signOut } = useEasybase();
    const { switchToSignin } = useContext(AccountContext);
    function logout() {
        return signOut;
    }

    function returnToSignIn() {
        return switchToSignin;
    }

    return (
        <EasybaseProvider ebconfig = {ebconfig}>
            <BoxContainer>
                <SubmitButton onClick={function() {returnToSignIn(); logout();}}>Signin</SubmitButton>
                <Marginer direction="vertical" margin="1em" />
            </BoxContainer>
        </EasybaseProvider>
    );

}
