import React, { Component } from "react";
import { withRouter } from 'react-router-dom';
import { compose } from 'recompose';
import { BoxContainer, FormContainer, Input, SubmitButton, MutedLink, BoldLink } from "./common";
import { Marginer } from "../marginer";
import { AccountContext } from "./accountContent";
import { withFirebase } from '../firebase';

const SignInPage = () => (
    <LoginForm />
)

const INITIAL_STATE = {
    email: '',
    password: '',
    error: null,
};

class LoginFormBase extends Component {
    constructor(props) {
        super(props);

        this.state = { ...INITIAL_STATE };
    }

    onSubmit = event => {
        const { email, password } = this.state;

        this.props.firebase
            .doSignIn(email, password)
            .then(() => {
                this.setState({ ...INITIAL_STATE });
                this.props.history.push('/nutrition');
            })
            .catch(error => {
                this.setState({ error });
            });
        event.preventDefault();
    };

    onChange = event => {
        this.setState({ [event.target.name]: event.target.value });
    };

    render() {

        const { email, password, error } = this.state;

        const isInvalid = password === '' || email === '';

        return (
                <BoxContainer>
                    <FormContainer onSubmit={this.onSubmit}>
                        <Input name="email" type="email" value={email} placeholder="Email" onChange={this.onChange} />
                        <Input name="email" type="password" value={password} placeholder="Password" onChange={this.onChange} />
                    </FormContainer>
                    <Marginer direction="vertical" margin={5} />
                    <MutedLink href="#">Forgot your password?</MutedLink>
                    <Marginer direction="vertical" margin="1.6em" />
                    <SubmitButton disabled={isInvalid} type="submit">Signin</SubmitButton>
                    { error && <p>{error.message}</p>}
                    <Marginer direction="vertical" margin="1em" />
                    <MutedLink href="#">Don't have an account?<BoldLink href="#" onClick={switchToSignup}>Register</BoldLink></MutedLink>
                </BoxContainer>
        );
    }
}

const LoginForm = compose(
    withRouter,
    withFirebase,)(LoginFormBase);

export default SignInPage;

export { LoginForm }
