import React from 'react';
import './App.css';
import { useUser } from 'reactfire';
import Routes from './Routes/Routes';
import { AccountBox } from './accountBox';
import styled from "styled-components";

const AppContainer = styled.div`
    width: 100%;
    min-height: 100%;
    display: flex;
    position: relative;
    flex-direction: row;
    align-items: center;
    justify-content: center;
`;

function App() {
  const user = useUser();
  return (
    <div className = "App">
    {
      user &&
      <Routes />
    }
    {
      !user &&
      <AppContainer>
          <AccountBox />
      </AppContainer>
    }
    </div>
  );
}

export default App;
